INSERT INTO music_genres (name, description) VALUES
-- ROCK & METAL
('Rock', 'A broad genre of popular music that originated as "rock and roll" in the United States in the late 1940s and early 1950s.'),
('Hard Rock', 'A loosely defined subgenre of rock music that began in the mid-1960s, with the garage, psychedelic and blues rock movements.'),
('Heavy Metal', 'A genre of rock music that developed in the late 1960s and early 1970s, largely in the United Kingdom and the United States.'),
('Alternative Rock', 'A style of rock music that emerged from the independent music underground of the 1980s and became widely popular in the 1990s.'),
('Punk Rock', 'A rock music genre that developed in the mid-1970s in the United States, United Kingdom, and Australia.'),
('Grunge', 'An alternative rock genre and subculture that emerged during the mid-1980s in the American Pacific Northwest state of Washington.'),
('Progressive Rock', 'A broad genre of rock music that developed in the United Kingdom and United States throughout the mid-to-late 1960s.'),
('Indie Rock', 'A genre of rock music that originated in the United States and United Kingdom in the 1970s.'),
('Psychedelic Rock', 'A diverse style of rock music inspired, influenced, or representative of psychedelic culture.'),
('Glam Rock', 'A style of rock music that developed in the United Kingdom in the early 1970s performed by musicians who wore outrageous costumes, makeup, and hairstyles.'),
('Thrash Metal', 'An extreme subgenre of heavy metal music characterized by its overall aggression and often fast tempo.'),
('Death Metal', 'An extreme subgenre of heavy metal music. It typically employs heavily distorted and low-tuned guitars, played with techniques such as palm muting and tremolo picking.'),
('Black Metal', 'An extreme subgenre of heavy metal music. Common traits include fast tempos, a shrieking vocal style, heavily distorted guitars played with tremolo picking, raw recording, and unconventional song structures.'),
('Power Metal', 'A subgenre of heavy metal combining characteristics of traditional metal with speed metal, often within symphonic context.'),
('Nu Metal', 'A subgenre of alternative metal that combines elements of heavy metal music with elements of other genres such as hip hop, alternative rock, funk, industrial, and grunge.'),
('Metalcore', 'A fusion genre combining elements of extreme metal and hardcore punk.'),
('Symphonic Metal', 'A subgenre of heavy metal music that combines the heavy drums and guitars of metal with different elements of orchestral classical music.'),
('Folk Metal', 'A fusion genre of heavy metal music and traditional folk music that developed in Europe during the 1990s.'),
('Gothic Metal', 'A fusion genre that combines the heaviness of doom metal with the dark atmospheres of gothic rock.'),
('Industrial Metal', 'A musical genre that draws from industrial dance music, thrash metal and hardcore punk, using repeating metal guitar riffs, sampling, synthesizer or sequencer lines, and distorted vocals.'),
('Doom Metal', 'An extreme subgenre of heavy metal music that typically uses slower tempos, low-tuned guitars and a much "thicker" or "heavier" sound than other heavy metal genres.'),
('Stoner Rock', 'A fusion genre of rock music that combines elements of heavy metal and/or doom metal with psychedelic rock and acid rock.'),
('Post-Rock', 'A form of experimental rock characterized by a focus on exploring textures and timbre over traditional rock song structures, chords, or riffs.'),
('Shoegaze', 'A subgenre of indie and alternative rock characterized by its ethereal mixture of obscured vocals, guitar distortion and effects, feedback, and overwhelming volume.'),
('New Wave', 'A broad music genre that encompasses numerous pop-oriented styles from the late 1970s and the 1980s.'),
('Ska', 'A music genre that originated in Jamaica in the late 1950s and was the precursor to rocksteady and reggae.'),
('Pop Punk', 'A fusion music genre that combines elements of punk rock with power pop music.'),
('Emo', 'A style of rock music characterized by expressive, often confessional lyrics.'),
('Garage Rock', 'A raw and energetic style of rock and roll that flourished in the mid-1960s.'),
('Surf Rock', 'A subgenre of rock music associated with surf culture, particularly as found in Southern California.'),

-- POP
('Pop', 'A genre of popular music that originated in its modern form during the mid-1950s in the United States and the United Kingdom.'),
('Synth-pop', 'A subgenre of new wave music that first became prominent in the late 1970s and features the synthesizer as the dominant musical instrument.'),
('Electropop', 'A music genre combining elements of electronic and pop genres.'),
('Indie Pop', 'A music genre and subculture that combines guitar pop with DIY ethic in opposition to the style and tone of mainstream pop music.'),
('K-Pop', 'A genre of popular music originating in South Korea.'),
('J-Pop', 'A musical genre that entered the musical mainstream of Japan in the 1990s.'),
('Teen Pop', 'A subgenre of pop music that is oriented towards pre-teens and teenagers.'),
('Dance-Pop', 'A popular music subgenre that originated in the early 1980s. It is generally uptempo music intended for nightclubs with the intention of being danceable but also suitable for contemporary hit radio.'),
('Art Pop', 'A loosely defined style of pop music influenced by pop art''s integration of high and low culture, and which emphasizes the manipulation of signs, style, and appearance over personal expression.'),
('Baroque Pop', 'A fusion genre that combines rock and roll and pop music with classical music, specifically the baroque and classical eras.'),
('Bubblegum Pop', 'A genre of pop music with an upbeat sound contrived and marketed to appeal to pre-teens and teenagers.'),
('Christian Pop', 'A genre of popular music which is lyrically focused on matters concerned with the Christian faith.'),
('Latin Pop', 'A pop music subgenre that is a fusion of US-style music production with Latin American music genres from anywhere in Latin America and Spain.'),
('Country Pop', 'A subgenre of country music and pop music that was developed by members of the country genre out of a desire to reach a larger, mainstream audience.'),
('Disco', 'A genre of dance music and a subculture that emerged in the mid-1970s from the United States'' urban nightlife scene.'),

